import {P5} from "@tmcw/p5"

//P5 = require('https://unpkg.com/p5@1.2.0/lib/p5.js');

function* p5(sketch) {
  const element = DOM.element('div');

  yield element;

  const instance = new P5(sketch, element, true);

  try {
    while (true) {
      yield element;
    }
  } finally {
    instance.remove();
  }
}
